import create from 'zustand';
const useStore = create(set => ({
  count: 10,
  abc: false,
  colorActive: 'red',
  colorNot: 'blue',
  plus: () => set(state => ({
    count: state.count + 1
  })),
  clean: () => set({
    count: 0
  }),
  toggle: () => set(state => ({
    abc: !state.abc
  }))
}));
export default useStore;
