import create from 'zustand';
import { devtools } from 'zustand/middleware';

const useStore = create(
    devtools(set => ({
        count: 0,

        increaseCount: state => {
            set(state => {
                return { count: state.count + 1 };
            });
        },
    })),
);

function App() {
    const { count, inc } = useStore()


  return (
    <div className="App">
      <p>   카운터 갯수:{count}</p>
        <button onClick={inc}>증가</button>

    </div>
  );
}

export default App;
