import './App.css';
import {useState} from 'react';
import useStore from "./store";



function App() {

    const {count,plus,clean ,abc,toggle,colorActive} = useStore(state => state);


  return (
    <div className="App"  style={abc? {backgroundColor:colorActive} : {backgroundColor:'blue'}}>
      <p >   카운터 갯수:{count} </p>
        <button onClick={plus}> 증가시키기</button>
        <button onClick={clean}>초기화시키기</button>
        <button onClick={toggle}>색상체인지</button>

    </div>
  );
}

export default App;
