import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/scss';
import Modal from "../Modal";
import useUsers from "../../store/user";
import {useEffect, useRef, useState} from "react";
import {url} from "../../youtube/api";
import axios from "axios";
import '../../App.css';

function User() {
    const { users, execute} =useUsers(state => state);
    const [vids ,setVids] =useState([]);
    const [index, setIndex] =useState(0);
    const [isClick,setIsClick] =useState(false);
    const Modal = useRef(null);

    const callApi = (e) => {
        execute(`https://jsonplaceholder.typicode.com/users`)
        setIsClick(true);
    }
    const handleIdx =(idx) => {
        setIndex(idx);

    }
    console.log(idx);
    useEffect(() =>{
        axios.get(url).then((res) => {
           setVids(res.data.items);
           console.log(res);
        })


    },[])


    return(
        <div>
            <Swiper className='youtubeWrap' spaceBetween={20}
                  loop={true}
                    breakpoints={{
                        320: {
                            slidesPerView:2,
                            spaceBetween: 20,
                        },
                        1200: {
                            slidesPerView: 6,
                            spaceBetween: 10,
                        },
                    }}
            >
                {vids.map((vid,idx) =>{
                    if(idx > 10)  return;
                    const tit = vid.snippet.title;
                    const desc =vid.snippet.description
                    const thum =vid.snippet.thumbnails.maxres.url;
                    return (
                            <SwiperSlide key ={idx} className='youtube' >
                                <p><img src={thum} alt=""/></p>
                            </SwiperSlide>
                            )

                     })}
            </Swiper>
            <button onClick={callApi} disabled={isClick}> call api </button>
            <Swiper   spaceBetween={50}
                      slidesPerView={3}>
                {users.map((user) =>{
                    return  <SwiperSlide>key ={user.id}>{user.name}</SwiperSlide>
                })}
            </Swiper>

        </div>

    )
}

export default  User;
