import create from 'zustand';

const useTodoStore = create((set) => ({
    todos:[],
    addTodo:(newTodo) => set((state) =>({
        todos:[newTodo,...state.todos]
    })),
    removeTodo:(id) => set((state) =>({
        todos:state.todos.filter((todo ,i) => i!== id )
    }))
}))


export default  useTodoStore;
