import {useRef, useState} from 'react';
import useStore from "./store";



function App() {
    const Input = useRef();
    const {value,addPeople} = useStore(state => state);
    console.log(people);
const add =() => {
    addPeople(Input.current.value);
}
  return (
    <div className="App" >
        <input type="text" ref={Input}/>
        <button onClick={add}>추가하기</button>
        {value.map((p) => {
            return <div key={p.id}>{p.name}</div>
        })}

    </div>
  );
}

export default App;
