import './App.css';
import {useState} from 'react';
import useStore from "./store";



function App() {

    const {count,plus,clean ,abc} = useStore(state => state);
    const [isColor,setIsColor] = useState(abc);

  return (
    <div className="App" >
      <p style={isColor? {color:'red'} : {color:'blue'}}>   카운터 갯수:{count} </p>
        <button onClick={plus}> 증가시키기</button>
        <button onClick={clean}>초기화시키기</button>
        <button onClick={() => setIsColor(!isColor)}>색상체인지</button>

    </div>
  );
}

export default App;
